php.ini.njk:
date.time = {{ phpDateTimezone }} 
display_errors = {{ phpDisplayErrors }}
memory_limit = {{ phpMemoryLimit }} 
max_execution_time = {{ phpMaxExecutionTime }}
post_max_size = {{ phpPostMaxSize }}
upload_max_filesize = {{ phpUploadMaxFilesize }}

nginx-vhost.conf.njk:
server {
    listen 80 default_server;

    server_name _;

    root "{{ documentRoot }}";
    index index.php;

    client_max_body_size 50m;

    access_log   /docker.stdout;
    error_log    /docker.stderr warn;

    location / {
        try_files $uri $uri/ /index.php?$query_string;
    }

    location ~ \.php$ {
        fastcgi_split_path_info ^(.+\.php)(/.+)$;
        fastcgi_pass php;
        include fastcgi_params;
        fastcgi_param SCRIPT_FILENAME     $request_filename;
        fastcgi_read_timeout 600;
    }
}

Dockerfile.njk:
FROM webdevops/php-{{ webServer }}:{{ phpVersion }}{{ '-alpine' if alpine }}

COPY ./dockerizer/php.ini /opt/docker/etc/php/php.ini
{%- if webServer === "apache" %}
COPY ./dockerizer/vhost.conf /opt/docker/etc/httpd/vhost.conf
{%- endif %}
{%- if webServer === "nginx" %}
COPY ./dockerizer/vhost.conf /opt/docker/etc/nginx/vhost.conf
{%- endif %}
{%- if composerInstall %}
COPY composer.json composer.lock /app/
RUN composer install --no-interaction --no-scripts --no-suggest
{%- endif %}
COPY . /app
